mapper.update:
UPDATE members
SET mem_nm = #{memNm}, mem_als = #{memAls}, mem_tel = #{memTel}, mem_lotno_addr = #{memLotnoAddr}, mem_korn_road_nm = #{memKornRoadNm}, mem_addr_id = #{memAddrId}, mem_dtl_addr = #{memDtlAddr}
where mem_id = #{memId}

UPDATE members
SET use_yn = 'N'
WHERE mem_id = #{memId}

UPDATE members
SET mem_pw = #{memPw}
where mem_id =#{memId}
